import React,{ useState, useRef, useEffect } from 'https://cdn.skypack.dev/react';
import ReactDOM from 'https://cdn.skypack.dev/react-dom';
import { Button, Container } from 'https://cdn.skypack.dev/@material-ui/core';
import axios from 'https://cdn.skypack.dev/axios';
import styled from 'https://cdn.skypack.dev/styled-components@5.3.2';

const Wrapp = styled.div`
     font-family: monospace;
`;

const List = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 0;
  overflow: auto;
  display: flex;
  flex-direction: column-reverse;
`;

const ListItem = styled.li`
 padding: 0 5px;

  p {
    text-align: left;
    background: white;
    padding: 30px 15px;
  color: #2E3D49;
    border-radius: 10px;
    box-shadow: rgba(0, 0, 0, 0.25) 0px 0px 5px 1px;
  }
`;

const FixedValueList = ({data}) => {
  const [scrollableElements, setScrollableElements] = useState([]);
  const listRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "https://task-manager-nr-644bb62f65e54e1000000125.ubos.tech/getTasks"
        );
        setScrollableElements(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    if (data.isStartChat) {
      // Fetch data initially
      fetchData();

      // Fetch data every 10 seconds
      const interval = setInterval(() => {
        fetchData();
      }, 10000);

      // Clean up interval on component unmount
      return () => clearInterval(interval);
    }
  }, [data]);

  useEffect(() => {
    // Scroll to bottom after each update
    if (data.isStartChat) {
      listRef.current.scrollTop = listRef.current.scrollHeight;

    }
  }, [scrollableElements, data]);

  return (
<Wrapp>
          {data.isStartChat ? (
            <List ref={listRef}>
              <>
                {scrollableElements.length > 0 ? (
                  <>
                    {scrollableElements.map((item) => (
                      <ListItem key={item.id}>
                        <p> Current task: {item.task_name}</p>
                        <p> Result: {item.task_result}</p>
                        <p> Next task: {item.next_task}</p>
                        <p> Thinking...</p>
                      </ListItem>
                    ))}
                  </>
                ) : (
                  <ListItem>
                    <p> Loading...</p>
                  </ListItem>
                )}
              </>
            </List>
          ) : (
            <List>
              {scrollableElements.length > 0 ? (
                <List>
                  {scrollableElements.map((item) => (
                    <ListItem key={item.id}>
                      <p> Current task: {item.task_name}</p>
                      <p> Result: {item.task_result}</p>
                      <p> Next task: {item.next_task}</p>
                      <p> Thinking...</p>
                    </ListItem>
                  ))}
                </List>
              ) : (
                <ListItem>
                  <p>
                    {data.indexStatus
                      ? " Please enter goal"
                      : " Wait please. The table is still being initialized"}
                  </p>
                </ListItem>
              )}
            </List>
          )}
        </Wrapp>
  );
};

const MyCustomComponent = ({data, updateData, trigerParent}) => {
  console.log(data);
 return (
  <FixedValueList data={data}/>
  )
}

const ConnectedComponent = Tooljet.connectComponent(MyCustomComponent);
ReactDOM.render(<ConnectedComponent />, document.body);

